mapper.insert:
INSERT INTO post (
			post_no
			, post_id
			, post_date
			, post_views
			, post_content
			, user_id
		) VALUES (
			POST_LEJ_SEQ.NEXTVAL
			, #{postId}
			, SYSDATE
			, #{postViews}
			, #{postContent}
			, #{userId}
		)

mapper.update:
UPDATE post
		SET
			post_no = #{postNo}
		WHERE
			post_no = POST_LEJ_SEQ.NEXTVAL
			AND post_id = #{postId}
			AND post_date = SYSDATE
			AND post_views = #{postViews}
			AND post_content = #{postContent}
			AND user_id = #{userId}
