mapper.delete:
DELETE FROM exam
WHERE exam_id =#{examId}
  AND exam_state =#{examState}

mapper.update:
UPDATE exam SET
exam_nm = #{examNm},
exam_state = #{examState}, exam_date = SYSDATE WHERE exam_id =#{examId}

mapper.select:
SELECT * FROM (SELECT a.*, ROWNUM AS rn FROM (SELECT e.exam_id, e.exam_nm, e.exam_state, e.exam_date, l.les_id, l.les_cont, li.lidx_id, li.lidx_cont, l.tch_id FROM lesson l, lesson_index li, exam e WHERE l.les_id = li.les_id AND e.lidx_id(+) = li.lidx_id AND l.tch_id = #{tchId}
AND e.exam_id is NULL
AND e.exam_state = #{searchExamState}
ORDER BY e.exam_date DESC) a) WHERE rn BETWEEN 1 * (#{page} - 1) + 1 AND #{page} * 1
