mapper.delete:
DELETE FROM exam
WHERE exam_id =#{examId}
  AND exam_state =#{examState}

mapper.select:
SELECT * FROM (SELECT a.*, ROWNUM AS rn FROM (SELECT e.exam_id, e.exam_nm, e.exam_state, e.exam_date, e.lidx_id, e.les_id, li.lidx_cont, l.tch_id FROM exam e, lesson l, lesson_index li WHERE l.les_id = e.les_id AND li.lidx_id = e.lidx_id AND l.tch_id = #{tchId}
AND e.exam_state = #{searchExamState}
ORDER BY e.exam_date DESC) a) WHERE rn BETWEEN 1 * (${page} - 1) + 1 AND #{page} * 1
