mapper.select:
SELECT b.*, c.avgScore FROM (SELECT a.*, ROWNUM AS rn FROM (SELECT e.exam_id, e.exam_nm, e.exam_state, e.exam_date, l.les_id, l.les_cont, li.lidx_id, li.lidx_cont, NVL(es.es_fscore, 999) es_fscore, NVL(es.es_lscore, 999) es_lscore, NVL(es.es_fscore, 999) search_EsScore, es.es_edate, q.queTotal FROM pay p LEFT OUTER JOIN examscore es ON(p.user_id = es.user_id) LEFT OUTER JOIN exam e ON(p.les_id = e.les_id) LEFT OUTER JOIN lesson l ON(l.les_id = e.les_id) LEFT OUTER JOIN lesson_index li ON(li.les_id = l.les_id) LEFT OUTER JOIN (SELECT SUM(que_score) queTotal, exam_id FROM question GROUP BY exam_id) q ON(q.exam_id = e.exam_id) WHERE p.user_id = #{userId}
AND p.les_id = #{lesId}
AND es.es_fscore is NULL
AND es.es_fscore is NOT NULL
ORDER BY SUBSTR(li.lidx_id, 5)) a) b, (SELECT ROUND(AVG(es_fscore), 2) avgScore FROM examscore WHERE user_id = #{userId}) c WHERE rn BETWEEN 10 * (#{page} - 1) + 1 AND #{page} * 10

SELECT COUNT(*) FROM pay p LEFT OUTER JOIN exam e ON(p.les_id = e.les_id) WHERE p.user_id = ${userId}
AND e.les_id = #{lesId}
AND es.es_fscore is NULL
AND es.es_fscore is NOT NULL
