mapper.select:
SELECT * FROM (SELECT ROWNUM AS RN, A.* FROM (SELECT * FROM JOBINFO WHERE 1=1
AND JOBINFO_TITLE LIKE '%' || #{keyWord} || '%' AND JOBINFO_CONT LIKE '%' || #{keyWord} || '%')A)
)A)
WHERE RN BETWEEN (#{page} - 1) * #{pageSize} + 1 AND #{page} * #{pageSize}

mapper.update:
update jobInfo set
		jobinfo_title = #{jobinfoTitle}, jobinfo_cont = #{jobinfoCont}, jobinfo_udate = sysdate, jobinfo_uwriter = #{jobinfoUwriter} where jobInfo_Id =
#{jobinfoId}
