mapper.select:
		SELECT *
		  FROM lesson

			  AND sub_id=#{subId}
			  AND tch_id=#{tchId}
			  AND les_state='3'

 		   ORDER BY les_id ASC

SELECT  a.*, b.sub_nm 
  FROM lesson a,(select sub_nm from subject WHERE sub_id = #{subId}) b 

  	AND a.les_id=#{lesId}

mapper.update:
		UPDATE lesson 
   		   SET les_state='5' 

 			AND les_id=#{les_id}

		UPDATE lesson
   		   SET les_nm=#{lesNm}, les_cont=#{lesCont},les_cash=#{lesCash}, les_term=#{lesTerm}, les_state='1', les_detail=#{lesDetail}

		      AND les_id=#{lesId}

		UPDATE lesson
   		   SET les_state='2'

		      AND les_id=#{lesId}
