mapper.select:
SELECT * FROM lesson_index
AND les_id = #{lesId}
ORDER BY lidx_num ASC

mapper.update:
		UPDATE lesson_index
   		SET LIDX_CURTIME = #{lidxCurtime}

	   		AND LIDX_ID=#{lidxId}

mapper.select:
SELECT lidx_curtime
  FROM index_time

  	AND user_id=#{userId}
  	AND lidx_id=#{lidxId}

mapper.update:
UPDATE index_time
   SET lidx_curtime = #{lidxCurtime}

 	AND lidx_id = #{lidxId}
 	AND user_id = #{userId}
